'    );',
'    --',
'    IF in_filter_value IS NOT NULL THEN',
'        APEX_IG.ADD_FILTER (',
'            p_page_id           => app.get_page_id(),',
'            p_region_id         => region_id,',
'            p_column_name       => in_column_name,',
